$(document).ready(function(){
  $('.dropdown').hover(function(){
    $(this).toggleClass('active');
  });
  $('.twelve-sec-nav-btn').click(function(){
    $(this).parent('.twelve-sec-nav-it').toggleClass('active');
  });
  
  if($(window).width() >= '760'){
    $('.six-sec-slider').slick({
      dots: true,
      slidesToShow: 3,
      arrows: false,
      autoplay: true,
      autoplaySpeed: 5000,
      centerMode: true,
    });
  } else {
    $('.six-sec-slider').slick({
      dots: true,
      slidesToShow: 2,
      arrows: false,
      autoplay: true,
      autoplaySpeed: 5000,
      // centerMode: true,
    });
  }
  let action="click";
  let speed="500";
  $('li.sev-sec-fqa-i').on(action, function() {
    $(this).next().slideToggle(speed).siblings('li.sev-sec-fqa-i-s').slideUp();
  });
  
  
  if($(window).width() >= '760'){
    $('.eight-sec-slider').slick({
      dots: true,
      slidesToShow: 2,
      autoplay: true,
      autoplaySpeed: 4000,
      centerMode: true,
    });
  } else {
    $('.eight-sec-slider').slick({
      dots: true,
      slidesToShow: 1,
      autoplay: true,
      autoplaySpeed: 6000,
      arrows: false,
      // centerMode: true,
    });
  }
  if($(window).width() >= '760'){
    
    $('.twelve-sec-slider').slick({
      dots: true,
      arrows: false,
      slidesToShow: 2,
      autoplay: true,
      autoplaySpeed: 4000,
      // centerMode: true,
    });
  } else {
    
    $('.twelve-sec-slider').slick({
      dots: true,
      arrows: false,
      slidesToShow: 1,
      autoplay: true,
      autoplaySpeed: 4000,
      // centerMode: true,
    });
  }
  if($(window).width() <= '680' && $('.eight-sec-twelve').length) {
    $('.eight-sec-twelve').slick({
      dots: true,
      arrows: false,
      slidesToShow: 1,
      autoplay: true,
      autoplaySpeed: 4000,
      // centerMode: true,
    });
  }
  if($(window).width() <= '640' && $('.eleven-sec-slider').length) {
    $('.eleven-sec-slider').slick({
      dots: true,
      arrows: false,
      slidesToShow: 2,
      autoplay: true,
      autoplaySpeed: 4000,
      // centerMode: true,
    });
  }
  if($(window).width() >= '760' && $('.twelve-sec-right').length) {
    // console.log()
    $('.twelve-sec-right').css('maxWidth', `${$('.twelve-sec-box').width() - $('.twelve-sec-nav').width() - parseInt($('.twelve-sec-right').css('marginLeft').slice(0,-2))}px`)
  }
  if($(window).width() <= '460'){
    // console.log()
    $('.dropdown').on('click', function(){
      $('.dropdown').toggleClass('flexss')
    })
    // $('.twelve-sec-right').css('maxWidth', `${$('.twelve-sec-box').width() - $('.twelve-sec-nav').width() - parseInt($('.twelve-sec-right').css('marginLeft').slice(0,-2))}px`)
  }
})