const init=()=>{document.querySelectorAll(".addCall").forEach(e=>{e.addEventListener("click",e=>{e.preventDefault(),document.querySelector("#head__form").classList.toggle("active"),document.querySelector("body").classList.toggle("noScr")})}),document.querySelector("#head__exit").addEventListener("click",e=>{e.preventDefault(),document.querySelector("#head__form").classList.remove("active"),document.querySelector("body").classList.remove("noScr")}),document.querySelector("#nav-btn").addEventListener("click",e=>{e.preventDefault(),document.querySelector("#nav-btn").classList.toggle("active"),document.querySelector("#nav-bar").classList.toggle("active")}),document.querySelector("#games__btn").addEventListener("click",e=>{e.preventDefault(),document.querySelector("#games__btn").closest(".games__btn").remove(),document.querySelectorAll(".games__item").forEach(e=>{e.style.display="block",setTimeout(()=>{e.style.opacity="1"},100)})})};window.addEventListener("DOMContentLoaded",init);