const init=()=>{document.querySelector("#head__btn").addEventListener("click",e=>{e.preventDefault(),document.querySelector("#head__form").classList.toggle("active"),document.querySelector("body").classList.toggle("noScr")}),document.querySelector("#head__exit").addEventListener("click",e=>{e.preventDefault(),document.querySelector("#head__form").classList.remove("active"),document.querySelector("body").classList.remove("noScr")})};window.addEventListener("DOMContentLoaded",init);