$(document).ready(function() {
  $(".result-slider").slick({
    dots: true,
    infinite: true,
    speed: 700,
    appendDots: $('.result-slider-dots'),
    appendArrows: $('.result-slider-arrows'),
    autoplay: true,
    autoplaySpeed: 3400,
  })
  $(".result-com-slider").slick({
    dots: true,
    infinite: true,
    speed: 700,
    appendDots: $('.result-com-slider-dots'),
    appendArrows: $('.result-com-slider-arrows'),
    autoplay: true,
    autoplaySpeed: 3400,
  })
  $(".service-slider").slick({
    dots: true,
    infinite: true,
    speed: 700,
    rows: 2,
    appendDots: $('.service-slider-dots'),
    appendArrows: $('.service-slider-arrows'),
    autoplay: true,
    autoplaySpeed: 1400,
  })
  $(".clinic-slider").slick({
    dots: true,
    infinite: true,
    speed: 700,
    appendDots: $('.clinic-slider-dots'),
    appendArrows: $('.clinic-slider-arrows'),
    autoplay: true,
    autoplaySpeed: 1400,
  })
  $(".action-slider").slick({
    dots: true,
    infinite: true,
    speed: 700,
    appendDots: $('.action-slider-dots'),
    appendArrows: $('.action-slider-arrows'),
    autoplay: true,
    autoplaySpeed: 1400,
  })
  $(".instagram-slider").slick({
    dots: true,
    infinite: true,
    speed: 700,
    appendDots: $('.instagram-slider-dots'),
    appendArrows: $('.instagram-slider-arrows'),
    slidesToShow: 4,
    sliderToScroll: 2,
    autoplay: true,
    autoplaySpeed: 1400,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
        }
      }
    ]
  })
  $(".doctors-slider").slick({
    dots: true,
    infinite: false,
    speed: 400,
    appendDots: $('.doctors-slider-dots'),
    appendArrows: $('.doctors-slider-arrows'),
    slidesToShow: 2,
    autoplay: true,
    autoplaySpeed: 2000,
    
    responsive: [
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
        }
      }
    ]
  })
  $(window).scroll(function() {    
    var scroll = $(window).scrollTop();

    if (scroll >= 152) {
        $(".header").hide().addClass("headerStat").show();
    } else {
        $(".header").hide().removeClass("headerStat").show();
    }
  });
  $('.burger-menu').on('click', function() {
    $(this).toggleClass("burger-menu--opened");
    $(this).toggleClass("burger-menu--closed");
    $(".menu-wrap").toggleClass("active");
    $("body").toggleClass("bodyOver")
  });
});