const header=()=>{const e=document.querySelector(".header");if(e){e.querySelector(".header__btn").addEventListener("click",(i=>{i.preventDefault(),e.classList.contains("active")?(e.classList.remove("active"),document.body.style.overflow="auto"):(e.classList.add("active"),document.body.style.overflow="hidden")}))}},sliders=()=>{const e=e=>{const i=document.querySelector(e);if(i){const e=i.querySelector(".swiperWrapper"),s=i.querySelectorAll(".swiperSlide");e.classList.add("swiper-wrapper"),e.classList.remove("swiperWrapper"),s.forEach((e=>{e.classList.add("swiper-slide"),e.classList.remove("swiperSlide")}))}};if(window.innerWidth<767){e(".swiper1"),e(".swiper2"),e(".swiper3");new Swiper(".swiper1",{loop:!0,slidesPerView:"auto",centeredSlides:!0,spaceBetween:30,autoplay:{delay:5e3},pagination:{el:".swiper-pagination1",clickable:!0,renderBullet:function(e,i){return'<span class="'+i+'"></span>'}}}),new Swiper(".swiper2",{loop:!0,slidesPerView:"auto",centeredSlides:!0,spaceBetween:30,autoplay:{delay:5e3},pagination:{el:".swiper-pagination2",clickable:!0,renderBullet:function(e,i){return'<span class="'+i+'"></span>'}}}),new Swiper(".swiper3",{loop:!0,slidesPerView:"auto",centeredSlides:!0,spaceBetween:30,autoplay:{delay:5e3},pagination:{el:".swiper-pagination3",clickable:!0,renderBullet:function(e,i){return'<span class="'+i+'"></span>'}}})}},init=()=>{header(),sliders()};window.addEventListener("DOMContentLoaded",init);