const createSlider=e=>{const i=document.querySelector(e);i.querySelectorAll(".swiperSlide").forEach((e=>{e.classList.remove("swiperSlide"),e.classList.add("swiper-slide")}));const r=document.createElement("div"),t=document.createElement("div");t.classList="swiper-pagination",r.classList="swiper-wrapper",r.innerHTML=i.innerHTML,i.innerHTML="",i.appendChild(r),i.appendChild(t);new Swiper(e,{loop:!0,slidesPerView:"auto",centeredSlides:!0,pagination:{el:".swiper-pagination",clickable:!0}})},sliders=()=>{createSlider(".review__video"),createSlider(".review__photo")},init=()=>{window.innerWidth<980&&(createSlider(".review__video"),createSlider(".review__photo"))};window.addEventListener("DOMContentLoaded",init);