const basicScrollTop=function(){let o=document.querySelectorAll("[data-scroll]");const n=()=>{0!=window.scrollY&&setTimeout((function(){window.scrollTo(0,window.scrollY-30),n()}),1)};o.forEach((o=>{o.addEventListener("click",(()=>{n()}))}))},init=()=>{basicScrollTop()};window.addEventListener("DOMContentLoaded",init);