const videoControl=()=>{const e=document.querySelectorAll(".videoContainer");e&&e.forEach((e=>{const t=e.querySelector(".videoContainer__sound"),o=e.querySelector(".video-home");t.addEventListener("click",(e=>{e.preventDefault(),t.classList.contains("active")?(o.muted=!0,t.classList.remove("active")):(o.muted=!1,t.classList.add("active"))}))}))},init=()=>{videoControl()};window.addEventListener("DOMContentLoaded",init);